function isSummer(startMonth, startDay, endMonth, endDay){
    let dateArray = currentDate.split('-');
    console.log(dateArray);
    let currentMonth = dateArray[1];
    let currentDay = dateArray[2];
    if(dateArray[1].substring(0) == '0') currentMonth = dateArray[1].substring(1);
    if(dateArray[2].substring(0) == '0') currentDay = dateArray[2].substring(1);

    if ((currentMonth < startMonth || (currentMonth === startMonth && currentDay < targetDay)) || (currentMonth > endMonth || (currentMonth === endMonth && currentDay > targetDay))) return 0; // 0 = not Summer
    else return 1; // 1 = is summer
}
